import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import PlayImage from './assets/imgs/play.png';
import StopImage from './assets/imgs/stop.png';

function getTimeInMinutes(seconds) {
    let minutos = Math.floor(seconds / 60);
    return minutos.toLocaleString('pt-BR', { minimumIntegerDigits: 2 });
}

function getTimeSeconds(seconds) {
    let s = seconds % 60;
    return s.toLocaleString('pt-BR', { minimumIntegerDigits: 2 });
}

const Container = styled.div`
    display: flex;
    border: 1px solid black;
    border-radius: 10px;
    width: 200px;
    align-items: center;
    justify-content: center;
    flex-direction: column;
`;

const Visor = styled.p`
    font-family: calibri;
    font-weight: bold;
`;

const ButtonsContainer = styled.div`
    display: flex;
    gap: 10px;
`;

const Button = styled.img`
    width: 30px;
    height: 30px;
    object-fit: contain;
    border: 1px solid #00000011;
    border-radius: 5px;
    box-shadow: 0 0 8px #00000011;
    padding: 10px;
    `;

const Timer = () => {
    const [tempo, setTempo] = useState(0);

    useEffect(() => {
        const contador = setInterval(() => {
            setTempo(tempo + 1);
        }, 1000);
        console.log(tempo);
        return () => clearInterval(contador);
    });

    return (
        <Container>
            <Visor>
                {getTimeInMinutes(tempo)} : {getTimeSeconds(tempo)}
            </Visor>
            <ButtonsContainer>
                <Button src={PlayImage}></Button>
                <Button src={StopImage}></Button>
            </ButtonsContainer>
        </Container>
    )
};

export default Timer;
