import React, { useState, useEffect } from 'react';


const Timer = () => {
    const [tempo, setTempo] = useState(0);
    
    useEffect(() => {
        const contador = setInterval(() => {
            setTempo(tempo + 1);
        }, 1000);

        return () => clearInterval(contador);
    });

    return (
        <p>tempo decorrido: {tempo}</p>
    )
};

export default Timer;
