import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

function getTimeInMinutes(seconds) {
    let minutos = Math.floor(seconds / 60);
    return minutos.toLocaleString('pt-BR', { minimumIntegerDigits: 2 });
}

function getTimeSeconds(seconds) {
    let s = seconds % 60;
    return s.toLocaleString('pt-BR', { minimumIntegerDigits: 2 });
}

const Container = styled.div`
    display: flex;
    border: 1px solid black;
    border-radius: 10px;
    width: 200px;
    align-items: center;
    justify-content: center;
`;

const Visor = styled.p`
    font-family: calibri;
    font-weight: bold;
`;

const ButtonContainer = styled.div`
    border: 1px solid black;
`;

const Button = stlyed.p`
`;

const Timer = () => {
    const [tempo, setTempo] = useState(0);

    useEffect(() => {
        const contador = setInterval(() => {
            setTempo(tempo + 1);
        }, 1000);
        console.log(tempo);
        return () => clearInterval(contador);
    });

    return (
        <Container>
            <Visor>
                {getTimeInMinutes(tempo)} : {getTimeSeconds(tempo)}
            </Visor>
            <ButtonsContainer>
                <Button></Button>
            </ButtonsContainer>
        </Container>
    )
};

export default Timer;
